import React, { useState } from "react";

const useFlip = () => {
    const [isFlipped, setIsFacingUp] = useState(true);
    const flip = () => {
    setIsFacingUp(isUp => !isUp);
    };
    return [isFacingUp, flip];
};

export default useFlip;
